import React from "react";
import logo from "./logo.svg";
import "./App.css";
import MarkdownRenderer from "react-markdown-renderer";
import { ThemeProvider } from "emotion-theming";
import theme from "@rebass/preset";
import { Flex, Box, Text, Button } from "rebass";
import socketIOClient from "socket.io-client";

class App extends React.Component {
  //const [markdown, setMarkdown] = React.useState("");
  constructor(props) {
    super(props);
    this.state = {
      markdown: ""
    };
    this.handleMarkdownChange = this.handleMarkdownChange.bind(this);
  }

  handleMarkdownChange = event => {
    this.setState({ markdown: event.target.value }, () => {
      //console.log(JSON.stringify({"message": this.state.markdown}));
      this.ws.send(JSON.stringify({ message: this.state.markdown }));
    });
  };

  ws = new WebSocket("ws://192.168.1.13:8765/");

  componentWillMount() {
    this.ws.onopen = evt => {
      // on connecting, do nothing but log it to the console
      console.log("connected");
      //console.log(evt)
      //this.setState({markdown: evt.data.value})
    };

    this.ws.onmessage = evt => {
      // listen to data sent from the websocket server
      //const message = JSON.parse(evt.data)
      console.log(JSON.parse(evt.data)["value"]);
      const message = JSON.parse(evt.data)["value"];
      this.setState({ markdown: message });
    };

    this.ws.onclose = () => {
      console.log("disconnected");
      // automatically try to reconnect on connection loss
    };
  }

  render() {
    return (
      <div id="root" style={{ display: "flex", width: "100%", height: "100%" }}>
        <ThemeProvider theme={theme}>
          <Flex flexDirection={"column"}>
            <Flex flexShrink={1}>
              <Box px={2} py={1} width={1 / 2}>
                <Text fontSize={[3, 4, 5]} fontWeight="bold" color="primary">
                  Simple markdown preview app
                </Text>
                <Text fontSize={[3, 4, 5]} fontWeight="bold" color="black">
                  Simple markdown preview app
                </Text>
              </Box>
              <Box px={2} py={1} width={1 / 2}></Box>
            </Flex>
            <Flex flexGrow={1}>
              <Box p={3} width={1 / 2} color="white" bg="primary" flexGrow={1}>
                <textarea
                  autoFocus
                  value={this.state.markdown}
                  onChange={this.handleMarkdownChange}
                  style={{
                    fontFamily: "Helvetica",
                    color: "white",
                    border: "none",
                    width: "100%",
                    height: "100%",
                    backgroundColor: "transparent",
                    outline: "none",
                    resize: "none"
                  }}
                ></textarea>
              </Box>
              <Box p={3} width={1 / 2} color="black" bg="muted" flexGrow={1}>
                <Flex flexDirection={"column"} justifyContent={"space-between"} alignItems>
                  <Box px={2} py={1} width={1} flexGrow={1}>
                    <MarkdownRenderer markdown={this.state.markdown} />
                  </Box>
                  <Box px={2} py={1} width={1 / 2} flexShrink={1}>
                    <Button>Download as PDF</Button>
                  </Box>
                </Flex>
              </Box>
            </Flex>
          </Flex>
        </ThemeProvider>
      </div>
    );
  }
}

export default App;
